
var end = require('./lib/endpoints.json')
  , fs = require('fs');

var endpoints = [];

for(var i in end) {
  if(end[i].streaming)
  endpoints.push(i);
}

endpoints.sort();
var caca = "";

console.log(endpoints);
for(var i = 0; i < endpoints.length; i++) {
caca += "| " + endpoints[i] + " | " + end[endpoints[i]].resource + " |\n";
}

     fs.writeFile(__dirname + '/table.txt', caca);
